statements.select:
SELECT #CHNL_CD# AS CHNL_CD , RN , PUR_ORD_NO , PUR_ORD_SEQ , N1ST_ITM_GRP_NM , N2ND_ITM_GRP_NM , CO_CD , CO_NM , ITM_CD , ITM_NM , ITM_SZ_NM , UOM_NM , ITM_QTY AS TMP_RMK_QTY , PUR_QTY , RMN_QTY , CO_UT_PRC , ITM_QTY * CO_UT_PRC AS PUR_AMT , PUR_RMK , ITM_QTY , INAUD_ORG_NO , INAUD_ORG_NM FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY T1.AREA_CD ORDER BY T3.N1ST_ITM_GRP_CD, T3.ITM_CD, T1.PUR_DT ) AS RN , T2.PUR_ORD_NO , T2.PUR_ORD_SEQ , dbo.PUR_N1ST_GRP_NM_FNC(#CHNL_CD#, T3.ITM_GRP_CLSS_CD, T3.N1ST_ITM_GRP_CD) AS N1ST_ITM_GRP_NM , dbo.PUR_N2ST_GRP_NM_FNC(#CHNL_CD#,T3.ITM_GRP_CLSS_CD, T3.N1ST_ITM_GRP_CD, N2ND_ITM_GRP_CD) AS N2ND_ITM_GRP_NM , T1.PUR_CO_CD AS CO_CD , dbo.CD_COMPANY_NM_FNC(#CHNL_CD#,T1.PUR_CO_CD) AS CO_NM , T3.ITM_CD , T3.ITM_NM , T3.ITM_SZ_NM , dbo.CD_SYS_CLSS_FNC(#CHNL_CD#,'L-003', T3.UOM_CD) AS UOM_NM , T2.PUR_QTY , 0 AS RMN_QTY , T2.CO_UT_PRC , T2.PUR_AMT , T2.PUR_ITM_RMK AS PUR_RMK , (T2.PUR_QTY - ISNULL(dbo.FF_INV_ORD_QTY(#CHNL_CD#, T2.PUR_ORD_NO, T2.PUR_ORD_SEQ), 0)) AS ITM_QTY , #AREA_CD# AS INAUD_ORG_NO , dbo.CD_SYS_CLSS_FNC(#CHNL_CD#,'P-008', #AREA_CD#) AS INAUD_ORG_NM FROM TB_PUR_ORD T1 , TB_PUR_ORD_DTL T2 , TB_CD_ITM T3 WHERE 1 = 1 AND T1.CHNL_CD = #CHNL_CD# AND T1.CHNL_CD = T2.CHNL_CD AND T1.CHNL_CD = T3.CHNL_CD AND T1.PUR_ORD_NO = T2.PUR_ORD_NO AND T2.ITM_CD = T3.ITM_CD AND T2.PUR_QTY - ISNULL(dbo.FF_INV_ORD_QTY(#CHNL_CD#, T2.PUR_ORD_NO, T2.PUR_ORD_SEQ), 0) > 0 AND T1.PUR_CLZ_FLG = 'N' AND T1.PUR_DT >= #FM_DT# AND T1.PUR_DT <= #TO_DT# AND NOT EXISTS (SELECT 'X' FROM TB_PUR_CTRT_CLZ X WHERE 1 = 1 AND X.CHNL_CD = #CHNL_CD# AND X.ORD_NO = T2.PUR_ORD_NO AND X.ORD_SEQ = T2.PUR_ORD_SEQ )
AND T1.PUR_CO_CD = #CO_NO#
) T1 ORDER BY RN

SELECT #CHNL_CD# AS CHNL_CD , T1.INAUD_TMP_NO , T1.INAUD_TMP_SEQ , T1.INAUD_TP_NM , T1.CO_NO , T1.CO_NM , T1.ITM_CD , T1.ITM_NM , T1.ITM_SZ_NM , T1.LOC_CD , T1.LOC_NM , T1.ITM_QTY , T1.RMN_QTY AS TMP_RMK_QTY , T1.RMN_QTY , T1.ITM_WGT , T1.UOM_CD , T1.UOM_NM , T1.ITM_RMK , T1.IMP_ITM_PRC , T1.IMP_ITM_AMT , RMN_QTY AS IN_QTY , T1.IMP_CTRT_NO , T1.IMP_BL_NO , CASE WHEN T1.IMP_INV_NO IS NULL THEN dbo.FF_INAUD_TMP_RLSE_NO(#CHNL_CD#, T1.INAUD_TMP_NO, T1.INAUD_TMP_SEQ) ELSE T1.IMP_INV_NO END AS IMP_INV_NO , CASE WHEN GBN = 'A' THEN '수입' ELSE '국내' END AS GBN , CONVERT(VARCHAR(10), INAUD_DT, 121) AS INAUD_DT_NM FROM ( SELECT T1.INAUD_TMP_NO , T1.INAUD_TMP_SEQ , CASE WHEN SUBSTRING(T1.PUR_ORD_NO, 1, 2) = 'IV' THEN '수입' WHEN SUBSTRING(T1.PUR_ORD_NO, 1, 2) = 'PO' THEN '국내' END AS INAUD_TP_NM , T1.CO_CD AS CO_NO , dbo.CD_COMPANY_NM_FNC(#CHNL_CD#, T1.CO_CD) AS CO_NM , T1.ITM_CD , T2.ITM_NM , T2.ITM_SZ_NM , T1.LOC_CD , dbo.CD_SYS_CLSS_FNC(#CHNL_CD#, 'P-008', T1.LOC_CD) AS LOC_NM , T1.ITM_QTY , ISNULL(T1.ITM_QTY - dbo.FF_INVT_TMP_RMN_QTY(#CHNL_CD#, T1.INAUD_TMP_NO, T1.INAUD_TMP_SEQ), 0) AS RMN_QTY , ROUND(T1.ITM_QTY * T2.UOM_WGT, 0) AS ITM_WGT , T1.UOM_CD , dbo.CD_SYS_CLSS_FNC(#CHNL_CD#, 'L-003', T1.UOM_CD) AS UOM_NM , T1.ITM_RMK , T1.ITM_PRC AS IMP_ITM_PRC , T1.ITM_AMT AS IMP_ITM_AMT , ISNULL(T1.ITM_QTY, 0) AS IN_QTY , T1.PUR_ORD_NO , T1.PUR_ORD_SEQ , T3.IMP_CTRT_NO , T3.IMP_BL_NO , T3.IMP_INV_NO , T3.GBN , T1.INAUD_DT FROM TB_INAUD_TMP T1 LEFT OUTER JOIN ( SELECT GBN , IMP_CTRT_NO , IMP_BL_NO , IMP_INV_NO , IMP_INV_SEQ FROM ( SELECT 'A' AS GBN , MAX(T2.IMP_CTRT_NO) AS IMP_CTRT_NO , MAX(T3.IMP_BL_NO) AS IMP_BL_NO , T3.IMP_INV_NO , T1.IMP_INV_SEQ FROM TB_PUR_IMP_INV_DTL T1 , TB_PUR_IMP_CTRT T2 , TB_PUR_IMP_CTRT_DTL T4 , TB_PUR_IMP_INV T3 WHERE 1 = 1 AND T1.CHNL_CD = #CHNL_CD# AND T1.CHNL_CD = T2.CHNL_CD AND T1.CHNL_CD = T3.CHNL_CD AND T1.CHNL_CD = T4.CHNL_CD AND T2.IMP_PUR_NO = T4.IMP_PUR_NO AND T1.IMP_INV_NO = T3.IMP_INV_NO AND T1.IMP_PUR_NO = T4.IMP_PUR_NO AND T1.IMP_PUR_SEQ = T4.IMP_PUR_SEQ AND T2.IMP_CO_CD = #CO_NO# AND T2.IMP_CTRT_DT >= GETDATE() - 365 AND NOT EXISTS ( SELECT 'X' FROM TB_PUR_CTRT_CLZ X WHERE 1 = 1 AND X.CHNL_CD = #CHNL_CD# AND X.ORD_NO = T4.IMP_PUR_NO AND X.ORD_SEQ = T4.IMP_PUR_SEQ ) GROUP BY T3.IMP_INV_NO , T1.IMP_INV_SEQ UNION ALL SELECT 'B' AS GBN , T1.PUR_ORD_NO , NULL , T1.PUR_ORD_NO , T2.PUR_ORD_SEQ FROM TB_PUR_ORD T1 , TB_PUR_ORD_DTL T2 WHERE 1 = 1 AND T1.CHNL_CD = #CHNL_CD# AND T1.CHNL_CD = T2.CHNL_CD AND T1.PUR_ORD_NO = T2.PUR_ORD_NO AND T1.PUR_CO_CD = #CO_NO# AND T1.PUR_DT >= GETDATE() - 365 AND NOT EXISTS ( SELECT 'X' FROM TB_PUR_CTRT_CLZ X WHERE 1 = 1 AND X.CHNL_CD = #CHNL_CD# AND X.ORD_NO = T2.PUR_ORD_NO AND X.ORD_SEQ = T2.PUR_ORD_SEQ ) GROUP BY T1.PUR_ORD_NO , T2.PUR_ORD_SEQ ) T1 GROUP BY GBN , IMP_CTRT_NO , IMP_BL_NO , IMP_INV_NO , IMP_INV_SEQ ) T3 ON ( T1.PUR_ORD_NO = T3.IMP_INV_NO AND T1.PUR_ORD_SEQ = T3.IMP_INV_SEQ ) , TB_CD_ITM T2 WHERE 1 = 1 AND T1.CHNL_CD = #CHNL_CD# AND T1.CHNL_CD = T2.CHNL_CD AND T1.ITM_CD = T2.ITM_CD AND T1.CO_CD = #CO_NO# AND T1.INAUD_DT >= CAST(#FM_DT# + ' 00:00:00' AS DATETIME) AND T1.INAUD_DT <= CAST(#TO_DT# + ' 23:59:59' AS DATETIME)
AND SUBSTRING(T1.PUR_ORD_NO, 1, 2) LIKE #INAUD_TP_NM# + '%'
) T1 WHERE RMN_QTY <> 0
