statements.select:
SELECT ROW_NUMBER() OVER (PARTITION BY NULL ORDER BY T1.SL_RLSE_SEQ) AS RN , CASE WHEN DC_PRC_VAL <> SL_ITM_PRC_VAL THEN 1 ELSE 0 END AS DC_CHK , SL_ITM_CD , ITM_NM , ITM_SZ_NM , CONVERT(BIGINT, ISNULL(SL_ITM_QTY, 0)) AS SL_ITM_QTY , SL_ITM_WGT , CONVERT(FLOAT, ISNULL(SL_ITM_PRC_VAL, 0.0)) AS SL_ITM_PRC_VAL , CONVERT(FLOAT, ISNULL(DC_PRC_VAL, 0.0)) AS DC_PRC_VAL , CONVERT(BIGINT, ISNULL(SL_ITM_AMT, 0)) AS SL_ITM_AMT , SL_ITM_RMK , CHNL_CD , N1ST_ITM_GRP_NM FROM ( SELECT T1.SL_ITM_CD , T2.ITM_NM , T2.ITM_SZ_NM , T1.SL_ITM_QTY , T1.SL_ITM_PRC_VAL , T1.SL_ITM_PRC_DC_VAL AS DC_PRC_VAL , T1.SL_ITM_AMT , T1.SL_ITM_RMK , T1.SL_RLSE_NO , T1.SL_RLSE_SEQ , ROUND(T1.SL_ITM_QTY * T2.UOM_WGT, 0) AS SL_ITM_WGT , T1.CHNL_CD , dbo.PUR_N1ST_GRP_NM_FNC(T1.CHNL_CD, T2.ITM_GRP_CLSS_CD, T2.N1ST_ITM_GRP_CD) AS N1ST_ITM_GRP_NM FROM TB_SL_RLSE_DTL T1 , TB_CD_ITM T2 WHERE 1 = 1 AND T1.SL_RLSE_NO = #SL_RLSE_NO# AND T1.CHNL_CD = #CHNL_CD# AND T1.CHNL_CD = T2.CHNL_CD AND T1.SL_ITM_CD = T2.ITM_CD AND T1.SL_ITM_QTY <> 0
AND T1.SL_RLSE_SEQ = #SL_RLSE_SEQ#
) T1
